import { Component } from '@angular/core';
import { Book } from '../model/Book';
import { BookService } from '../shared/Book.service';

@Component({
  selector: 'app-modificar',
  templateUrl: './modificar.component.html',
  styleUrls: ['./modificar.component.css']
})
export class ModificarComponent {
  bookId: number;
  book: Book = new Book();

  constructor(private bookService: BookService) {}

  buscarLibro() {
    this.bookService.findBook(this.bookId).subscribe(
      (book: Book) => {
        this.book = book;
      },
      (error) => {
        console.log('Error al buscar el libro:', error);
      }
    );
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Modificar Libro</title>
  <style>
    body {
      font-family: Arial, sans-serif;
    }

    label {
      display: block;
      margin-bottom: 5px;
    }

    input[type="text"] {
      width: 300px;
      padding: 5px;
    }

    input[type="submit"] {
      padding: 10px 20px;
      background-color: #4CAF50;
      color: white;
      border: none;
      cursor: pointer;
    }

    input[type="submit"]:hover {
      background-color: #45a049;
    }
  </style>
</head>
<body>
  <h2>Modificar Libro</h2>
  <form>
    <label for="title">Title:</label>
    <input [(ngModel)]="book.title" type="text" id="title" name="title" required>

    <label for="reference">Reference:</label>
    <input [(ngModel)]="book.reference" type="text" id="reference" name="reference" required>

    <label for="author">Author:</label>
    <input [(ngModel)]="book.author" type="text" id="author" name="author" required>

    <label for="price">Price:</label>
    <input [(ngModel)]="book.price" type="text" id="price" name="price" required>

    <label for="location">Location:</label>
    <input [(ngModel)]="book.location" type="text" id="location" name="location" required>

    <input type="submit" value="Modificar">
  </form>
</body>
</html>
