import { Component } from '@angular/core';
import { Book } from '../model/Book';
import { BookService } from '../shared/Book.service'; 

@Component({
  selector: 'app-lista-libro',
  templateUrl: './lista-libro.component.html',
  styleUrls: ['./lista-libro.component.css']
})
export class ListaLibroComponent {

  book: Book = new Book();
  books: Book[] = [];  

  constructor(private BookService: BookService) { }

  ngOnInit(): void {
    this.BookService.read().subscribe(books => this.books = books) 
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>List of Books</title>
  <style>
    body {
      font-family: Arial, sans-serif;
    }

    table {
      border-collapse: collapse;
      width: 100%;
    }

    th, td {
      padding: 8px;
      text-align: left;
      border-bottom: 1px solid #ddd;
    }

    th {
      background-color: #f2f2f2;
    }
  </style>
</head>
<body>
  <h2>List of Books</h2>
  <table>
    <thead>
      <tr>
        <th>Title</th>
        <th>Reference</th>
        <th>Author</th>
        <th>Price</th>
        <th>Location</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td class="td-read">{{book.title}}</td>
        <td class="td-read">{{book.reference}}</td>
        <td class="td-read">{{book.author}}</td>
        <td class="td-read">{{book.price}}</td>
        <td class="td-read">{{book.location}}</td>
      </tr>
      <!-- Add more rows for additional books -->
    </tbody>
  </table>
</body>
</html>
