import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { BookService } from '../shared/Book.service'; 
import { Book } from '../model/Book';

@Component({
  selector: 'app-libro',
  templateUrl: './libro.component.html',
  styleUrls: ['./libro.component.css']
})
export class LibroComponent {

  book: Book = new Book();

  constructor(private BookService: BookService) {
  }

  ngOnInit() {
  }

  btnRegistrar():void{
    this.BookService.create(this.book).subscribe()
    console.log(this.book.title)
    console.log(this.book.reference)
    console.log(this.book.author)
    console.log(this.book.price)
    console.log(this.book.location)
  }

}
<!DOCTYPE html>
<html>
<head>
  <title>Book Information Form</title>
  <style>
    body {
      font-family: Arial, sans-serif;
    }

    label {
      display: block;
      margin-bottom: 5px;
    }

    input[type="text"] {
      width: 300px;
      padding: 5px;
    }

    input[type="submit"] {
      padding: 10px 20px;
      background-color: #4CAF50;
      color: white;
      border: none;
      cursor: pointer;
    }

    input[type="submit"]:hover {
      background-color: #45a049;
    }
  </style>
</head>
<body>
  <h2>Book Information</h2>
  <form [formGroup]="form" novalidate>
    <label for="title">Title:</label>
    <input type="text" id="title" name="title" [(ngModel)]="book.title" name="title">

    <label for="reference">Reference:</label>
    <input type="text" id="reference" name="reference" [(ngModel)]="book.reference" name="reference">

    <label for="author">Author:</label>
    <input type="text" id="author" name="author" required [(ngModel)]="book.author" name="author">

    <label for="price">Price:</label>
    <input type="text" id="price" name="price" required [(ngModel)]="book.price" name="price">

    <label for="location">Location:</label>
    <input type="text" id="location" name="location" required [(ngModel)]="book.location" name="location">

    <input type="submit" value="Submit" (click)="btnRegistrar()">
  </form>
</body>
</html>
