import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { BookService } from '../shared/Book.service'; 
import { Book } from '../model/Book';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-libro',
  templateUrl: './libro.component.html',
  styleUrls: ['./libro.component.css']
})


export class LibroComponent {

  book: Book = new Book();

  constructor(private bookService: BookService, private actRouter:ActivatedRoute,private router: Router) {}

  ngOnInit() {
  }

  btnRegistrar():void{
    this.bookService.create(this.book).subscribe(res => this.router.navigate(['/listalibro']))
    console.log(this.book.title)
    console.log(this.book.reference)
    console.log(this.book.author)
    console.log(this.book.price)
    console.log(this.book.location)
  }

}
<!DOCTYPE html>
<html>
<head>
  <title>Book Information Form</title>
  <style>
    body {
      font-family: Arial, sans-serif;
    }

    label {
      display: block;
      margin-bottom: 5px;
    }

    input[type="text"] {
      width: 300px;
      padding: 5px;
    }

    input[type="submit"] {
      padding: 10px 20px;
      background-color: #4CAF50;
      color: white;
      border: none;
      cursor: pointer;
    }

    input[type="submit"]:hover {
      background-color: #45a049;
    }
  </style>
</head>
<body>
  <h2>Book Information</h2>
  <form>
    <label for="title">Title:</label>
    <input [(ngModel)]="book.title" type="text" id="title" name="title" required name="title">

    <label for="reference">Reference:</label>
    <input [(ngModel)]="book.reference" type="text" id="reference" name="reference" required name="reference">

    <label for="author">Author:</label>
    <input [(ngModel)]="book.author" type="text" id="author" name="author" required  name="author">

    <label for="price">Price:</label>
    <input [(ngModel)]="book.price" type="text" id="price" name="price" required  name="price">

    <label for="location">Location:</label>
    <input [(ngModel)]="book.location" type="text" id="location" name="location" required  name="location">

    <input type="submit" value="Submit" (click)="btnRegistrar()">
  </form>
</body>
</html>
