import { Component } from '@angular/core';
import { Book } from '../model/Book';
import { BookService } from '../shared/Book.service'; 
import { Router } from '@angular/router';

@Component({
  selector: 'app-lista-libro',
  templateUrl: './lista-libro.component.html',
  styleUrls: ['./lista-libro.component.css']
})
export class ListaLibroComponent {

  book: Book = new Book();
  books: Book[] = [];  

  constructor(private BookService: BookService, private router: Router) { }

  ngOnInit(): void {
    this.BookService.read().subscribe(books => this.books = books) 
  }

  editBook(id: number):void{
    this.router.navigate(['/modificar'], { queryParams: { id: id } })
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>List of Books</title>
  <style>
    body {
      font-family: Arial, sans-serif;
    }

    table {
      border-collapse: collapse;
      width: 100%;
    }

    th, td {
      padding: 8px;
      text-align: left;
      border-bottom: 1px solid #ddd;
    }

    th {
      background-color: #f2f2f2;
    }
  </style>
</head>
<body>
  <h2>List of Books</h2>
  <table>
    <thead>
      <tr>
        <th>Title</th>
        <th>Reference</th>
        <th>Author</th>
        <th>Price</th>
        <th>Location</th>
        <th>Options</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let book of books">
        <td>{{book.title}}</td>
        <td>{{book.reference}}</td>
        <td>{{book.author}}</td>
        <td>{{book.price}}</td>
        <td>{{book.location}}</td>
        <td><button (click)="editBook(book.id)">Edit</button></td>
      </tr>
      <!-- Add more rows for additional books -->
    </tbody>
  </table>
</body>
</html>
